package com.example.lab18.test4

import android.os.Bundle
import androidx.appcompat.app.AppCompatActivity
import com.bumptech.glide.Glide
import com.example.lab18.R
import com.example.lab18.databinding.ActivityTest4Binding

class Test4Activity : AppCompatActivity() {
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        val binding = ActivityTest4Binding.inflate(layoutInflater)
        setContentView(binding.root)

        val url = "data:image/png;base64,"
        Glide.with(this)
            .load(url)
            .override(200, 200)
            .placeholder(R.drawable.loading)
            .error(R.drawable.error)
            .into(binding.imageView)
    }
}